import requests
import os

user = 'openscan'
pw = 'free'

server = 'http://openscanfeedback.dnsuser.de:1334/'
basepath="/home/pi/shared/ui/data/zip/"

with open('/home/pi/shared/log/token.log', 'r') as file:
    token = file.read().strip('\n')


def OpenScanCloud(cmd, msg):
    r = requests.get(server + cmd, auth=(user, pw), params=msg)
    if r.status_code == 200:
        return r.json()
    else:
        return {}

msg={}
projects = []
for i in os.listdir(basepath):
    if os.path.isdir(basepath+ '/'+i):
        if os.path.isfile(basepath+'/'+i+'/status'):
            with open(basepath+'/'+i+'/status', 'r') as file:
                status = file.read().strip('\n')
                if status in ['expired', 'processing done', 'processing failed']:
                    continue
        projects.append(i)

for p in projects:
    answer = OpenScanCloud('getProjectInfo',{'token':token, 'project':p+'.zip'})
    if answer == {}:
        os.system('sudo rm -r ' + basepath + '/' + p)
    else:
        with open(basepath + '/' + p + '/status', 'w+') as file:
            file.write(answer['status'])
        with open(basepath + '/' + p + '/download', 'w+') as file:
            file.write(answer['dlink'])

msg1 = OpenScanCloud('getTokenInfo', {'token':token})

msg['list'] = projects
